<template>
  <div class="loading_box">
    <div class="mask" v-if="mask"></div>
    <div class="loading_content" :class="loadingClass">
      <img class="loading_icon" :src="loadingIcon" />
      <div class="loading_title" v-if="title">{{ title }}</div>
    </div>
  </div>
</template>

<script setup lang="ts">
import { ref } from "vue";

withDefaults(defineProps<{ title?: string; mask?: boolean }>(), {
  title: "",
  mask: false,
});

const loadingIcon =
  "data:image/svg+xml;base64,";
const loadingClass = ref("");

function close() {
  return new Promise((resolve, reject) => {
    loadingClass.value = "loading_fade_out";
    setTimeout(() => {
      resolve(null);
    }, 200);
  });
}

defineExpose({
  close,
});
</script>

<style scoped lang="less">
@keyframes loading-fade-out {
  0% {
    opacity: 1;
  }
  100% {
    opacity: 0;
  }
}
@keyframes uni-loading {
  0% {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(1turn);
  }
}
.loading_box {
  .mask {
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    background-color: rgba(255, 255, 255, 0);
  }
  .loading_content {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    z-index: 9999;
    display: flex;
    flex-direction: column;
    align-items: center;
    min-width: 200px;
    max-width: 260px;
    border-radius: 8px;
    background-color: rgba(0, 0, 0, 0.5);
    padding: 30px 16px;
    .loading_icon {
      display: block;
      width: 76px;
      height: 76px;
      animation: uni-loading 1s steps(12) infinite;
    }
    .loading_title {
      width: 100%;
      margin-top: 10px;
      font-size: 18px;
      color: #fff;
      text-align: center;
      white-space: nowrap;
      overflow: hidden;
      text-overflow: ellipsis;
    }
  }
  .loading_fade_out {
    animation: loading-fade-out 0.21s linear infinite;
  }
}
</style>
